/** 
 * @file requestmanager.js
 * @author Jeffrey Wilcke <jeff@ethdev.com>
 * @author Marek Kotewicz <marek@ethdev.com>
 * @author Marian Oancea <marian@ethdev.com>
 * @author Fabian Vogelsteller <fabian@ethdev.com>
 * @author Gav Wood <g@ethdev.com>
 * @date 2014
 */
var Jsonrpc = require('./jsonrpc');

var utils = require('../utils/utils');

var c = require('../utils/config');

var errors = require('./errors');
/**
 * It's responsible for passing messages to providers
 * It's also responsible for polling the ethereum node for incoming messages
 * Default poll timeout is 1 second
 * Singleton
 */


var RequestManager = function RequestManager(provider) {
  this.provider = provider;
  this.polls = {};
  this.timeout = null;
};
/**
 * Should be used to synchronously send request
 *
 * @method send
 * @param {Object} data
 * @return {Object}
 */


RequestManager.prototype.send = function (data) {
  if (!this.provider) {
    console.error(errors.InvalidProvider());
    return null;
  }

  var payload = Jsonrpc.toPayload(data.method, data.params);
  var result = this.provider.send(payload);

  if (!Jsonrpc.isValidResponse(result)) {
    throw errors.InvalidResponse(result);
  }

  return result.result;
};
/**
 * Should be used to asynchronously send request
 *
 * @method sendAsync
 * @param {Object} data
 * @param {Function} callback
 */


RequestManager.prototype.sendAsync = function (data, callback) {
  if (!this.provider) {
    return callback(errors.InvalidProvider());
  }

  var payload = Jsonrpc.toPayload(data.method, data.params);
  this.provider.sendAsync(payload, function (err, result) {
    if (err) {
      return callback(err);
    }

    if (!Jsonrpc.isValidResponse(result)) {
      return callback(errors.InvalidResponse(result));
    }

    callback(null, result.result);
  });
};
/**
 * Should be called to asynchronously send batch request
 *
 * @method sendBatch
 * @param {Array} batch data
 * @param {Function} callback
 */


RequestManager.prototype.sendBatch = function (data, callback) {
  if (!this.provider) {
    return callback(errors.InvalidProvider());
  }

  var payload = Jsonrpc.toBatchPayload(data);
  this.provider.sendAsync(payload, function (err, results) {
    if (err) {
      return callback(err);
    }

    if (!utils.isArray(results)) {
      return callback(errors.InvalidResponse(results));
    }

    callback(err, results);
  });
};
/**
 * Should be used to set provider of request manager
 *
 * @method setProvider
 * @param {Object}
 */


RequestManager.prototype.setProvider = function (p) {
  this.provider = p;
};
/**
 * Should be used to start polling
 *
 * @method startPolling
 * @param {Object} data
 * @param {Number} pollId
 * @param {Function} callback
 * @param {Function} uninstall
 *
 * @todo cleanup number of params
 */


RequestManager.prototype.startPolling = function (data, pollId, callback, uninstall) {
  this.polls[pollId] = {
    data: data,
    id: pollId,
    callback: callback,
    uninstall: uninstall
  }; // start polling

  if (!this.timeout) {
    this.poll();
  }
};
/**
 * Should be used to stop polling for filter with given id
 *
 * @method stopPolling
 * @param {Number} pollId
 */


RequestManager.prototype.stopPolling = function (pollId) {
  delete this.polls[pollId]; // stop polling

  if (Object.keys(this.polls).length === 0 && this.timeout) {
    clearTimeout(this.timeout);
    this.timeout = null;
  }
};
/**
 * Should be called to reset the polling mechanism of the request manager
 *
 * @method reset
 */


RequestManager.prototype.reset = function (keepIsSyncing) {
  /*jshint maxcomplexity:5 */
  for (var key in this.polls) {
    // remove all polls, except sync polls,
    // they need to be removed manually by calling syncing.stopWatching()
    if (!keepIsSyncing || key.indexOf('syncPoll_') === -1) {
      this.polls[key].uninstall();
      delete this.polls[key];
    }
  } // stop polling


  if (Object.keys(this.polls).length === 0 && this.timeout) {
    clearTimeout(this.timeout);
    this.timeout = null;
  }
};
/**
 * Should be called to poll for changes on filter with given id
 *
 * @method poll
 */


RequestManager.prototype.poll = function () {
  /*jshint maxcomplexity: 6 */
  this.timeout = setTimeout(this.poll.bind(this), c.ETH_POLLING_TIMEOUT);

  if (Object.keys(this.polls).length === 0) {
    return;
  }

  if (!this.provider) {
    console.error(errors.InvalidProvider());
    return;
  }

  var pollsData = [];
  var pollsIds = [];

  for (var key in this.polls) {
    pollsData.push(this.polls[key].data);
    pollsIds.push(key);
  }

  if (pollsData.length === 0) {
    return;
  }

  var payload = Jsonrpc.toBatchPayload(pollsData); // map the request id to they poll id

  var pollsIdMap = {};
  payload.forEach(function (load, index) {
    pollsIdMap[load.id] = pollsIds[index];
  });
  var self = this;
  this.provider.sendAsync(payload, function (error, results) {
    // TODO: console log?
    if (error) {
      return;
    }

    if (!utils.isArray(results)) {
      throw errors.InvalidResponse(results);
    }

    results.map(function (result) {
      var id = pollsIdMap[result.id]; // make sure the filter is still installed after arrival of the request

      if (self.polls[id]) {
        result.callback = self.polls[id].callback;
        return result;
      } else return false;
    }).filter(function (result) {
      return !!result;
    }).filter(function (result) {
      var valid = Jsonrpc.isValidResponse(result);

      if (!valid) {
        result.callback(errors.InvalidResponse(result));
      }

      return valid;
    }).forEach(function (result) {
      result.callback(null, result.result);
    });
  });
};

module.exports = RequestManager;
